  state = {
    data: [],
    name: [],
    requirements: [],
    pic_url: [],
    badgeClassID: []
  }

  componentDidMount() {
    axios.get(`http://localhost:8081/badges`)
      .then(res => {
        console.log(res.data.length);
        // for(let i = 0; i < res.data.length; i++) {
