import '../SignIn.css';
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword
} from "firebase/auth";
import { auth } from '../firebase.js';
import React, { useState } from 'react'
import Tabs from 'react-bootstrap/Tabs';
import Tab from 'react-bootstrap/Tab'
import Button from 'react-bootstrap/Button';

const SignIn = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [key, setKey] = useState('home');
    const register = async () => {
        try {
            const user = await createUserWithEmailAndPassword(
                auth,
                email,
                password
            );
        } catch (error) {
            const errorCode = error.code;
            const errorMessage = error.message;
            console.log(errorCode);
            console.log(errorMessage);
        }
    };

    const login = async () => {
        try {
            const user = await signInWithEmailAndPassword(
                auth,
                email,
                password
            );
        } catch (error) {
            const errorCode = error.code;
            const errorMessage = error.message;
            console.log(errorCode);
            console.log(errorMessage);
        }
    };

    return (
        <div className="signin">
            <Tabs
                id="controlled-tab-example"
                activeKey={key}
                onSelect={(k) => setKey(k)}
                className="mb-3"
            >
                <Tab eventKey="signin" title="Sign In">
                    <h1>Sign In</h1>
                    <input placeholder="email" type="email" onChange={(event) => {
                        setEmail(event.target.value);
                    }} />
                    <input placeholder="password" type="password" onChange={(event) => {
                        setPassword(event.target.value);
                    }} />
                    <Button variant="outline-secondary" onClick={login}> Sign In</Button>
                </Tab>
                <Tab eventKey="signup" title="Sign Up">
                    <h1>Sign Up</h1>
                    <input placeholder="email" type="email" onChange={(event) => {
                        setEmail(event.target.value);
                    }} />
                    <input placeholder="password" type="password" onChange={(event) => {
                        setPassword(event.target.value);
                    }} />
                    <Button variant="outline-secondary" onClick={register}>Sign Up</Button>
                </Tab>
                <Tab eventKey="forgetpassword" title="forget password">
                    <h1>Sign Up</h1>
                </Tab>
            </Tabs>


        </div>
    )
}
export default SignIn;
