import React, { useState } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './components/Home';
import SignIn from './components/SignIn';
import TeacherAdministration from './components/TeacherHome';
import BadgeAdmin from './components/BadgeAdmin';
import RewardAdmin from './components/RewardAdmin';
import UploadReward from './components/UploadReward';
import MazeAdmin from './components/MazeAdmin';
import EditMaze from './components/EditMazeContent';
import EditBadge from './components/EditBadge';
import {
  onAuthStateChanged,
} from "firebase/auth";
import { auth } from './firebase.js';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
  const [user, setUser] = useState({});
  onAuthStateChanged(auth, (currentUser) => {
    try {
      setUser(currentUser);
    } catch (error) {
      const errorCode = error.code;
      const errorMessage = error.message;
      console.log(errorCode);
      console.log(errorMessage);
    }
  });

  return (
    // {user ? <Home />:<SignIn />}
    <BrowserRouter basename="/quizment">
      <Routes>
        <Route path="/quizment" element={<SignIn />}></Route>
        <Route path="/quizment/studentDashboard" element={<Home />}></Route>
        <Route path="/quizment/teacherAdmin" element={<TeacherAdministration/>}></Route>
        <Route path="/quizment/badgesAdmin" element={<BadgeAdmin/>}></Route>
        <Route path="/quizment/rewardsAdmin" element={<RewardAdmin/>}></Route>
        <Route path="/quizment/uploadReward" element={<UploadReward/>}></Route>
        <Route path="/quizment/mazeAdmin" element={<MazeAdmin/>}></Route>
        <Route path="/quizment/EditMazeContent" element={<EditMaze/>}></Route>
        <Route path="/quizment/EditBadge" element={<EditBadge/>}></Route>
      </Routes>
    </BrowserRouter> 
  );
}

export default App;
