import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import Button from 'react-bootstrap/Button';


export default class BadgeAdmin extends React.Component {
  state = {
    data: [],
    class: [],
    name: '',
    requirements: '',
    pic_url: '',
    badgeClassID: ''
  }

  componentDidMount() {
    axios.get(`http://localhost:8081/quiz`)
      .then(res => {
        console.log(res.data.length);

        this.setState({ data: res.data });
      })




      function createCard(cardInfo) {
        var card = `
            <div class="card col-sm-4" style="margin-top: 2rem;">
                <div class="card-body" style="height:410px ">
                    <img src="${cardInfo.url}" style="width:333px;height:400px">
                   
                </div> <p class="card-text" style="padding:20px;font-size:20px">${cardInfo.title}</p>
                <div class="card-footer text-muted">
                    ${cardInfo.description}
                </div>
                <div class="card-footer text-muted">
                    ${cardInfo.price}
                </div>
                <div class="card-footer text-muted">
                    ${cardInfo.platform}
                </div>

                <div class="card-footer text-muted">
                    <a href="/viewGame.html?gameid=${cardInfo.gameid}" class="btn btn-primary">View</a>
                                
                </div>
            </div>
            `;

        return card;
    }
   
  }




  render() {
    const data = this.state.data;
    
    return (
<div>

{data && data.map(item =>
                            <div class="card" style=" width:60% ;margin-right:10px;">
                            <div class="card-body">
                               
                               
                            </div> <p class="card-text" style=" font-size:20px;margin-left:20px">Comment:${item.quizID}</p>
                            <div style="margin-left:20px "> 
                            <div >
                                ratings:${item.totalMarks}
                            </div>
                            <div >
                                username:${item.totalPoints}
                            </div>
                            
                        </div>
        
                            
                        </div>
                        )}
</div>
      
    )
  }
}
