      let id = null;
      const elem = document.getElementById("animate");
      let posT = 68;
      let posL = 30;
      clearInterval(id);
      id = setInterval(frame, 50);
      function frame() {
        if (posL === 50) {
          clearInterval(id);
          if (mazeLvl > 4) {
            setTimeout(lvl5(mazeLvl), 2);
          }
        } else {
          // posT--;
          //have it move up more
          posT -= 3
          posL++;
          elem.style.top = posT + "%";
          console.log(posT); console.log(posL)
          elem.style.left = posL + "%";
        }
      }
    }
    function lvl5(mazeLvl) {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 8;
      let posL = 50;
      elem.style.left = posL + "%";
      clearInterval(id);
      id = setInterval(frame, 50);
      function frame() {
        if (posT === 60) {
          clearInterval(id);
