      }
    }

    function lvl6(mazeLvl) {

      let id = null;
      const elem = document.getElementById("animate");
      let posT = 60;
      let posL = 50;
      elem.style.top = posT + "%";
      elem.style.left = posL + "%";
      clearInterval(id);
      id = setInterval(frame, 50);
      function frame() {
        if (posT === 10) {
          clearInterval(id);
          if (mazeLvl > 6) {
            setTimeout(lvl7(mazeLvl), 2);
          }

