import React from 'react';
import axios from 'axios';
import wisp from '../img/wisp.png';
import cloud1 from '../img/cloud1.png'; 
import cloud2 from '../img/cloud2.png'; 
import cloud3 from '../img/cloud3.png'; 
import cloud4 from '../img/cloud4.png'; 
import cloud5 from '../img/cloud5.png'; 
import cloud6 from '../img/cloud6.png'; 
import cloud7 from '../img/cloud7.png'; 
import cloud8 from '../img/cloud8.png'; 

import '../css/maze.css';


export default class BadgeAdmin extends React.Component {

  state = {
    data: [],
  maze:''
}


  componentDidMount() {

    axios.get(`http://localhost:8081/mazeContent`)
      .then(res => {
        console.log(res.data.length);
        this.setState({ data: res.data });

        console.log(res.data.length);

        this.setState({ data: res.data });


      });

      var studentID = 3;
      //GET STUDENT ID HERE
      axios.get(`http://localhost:8081/mapOfMaze/` + studentID)
          .then(res => {
         
           this.setState({maze:res.data[0].mazeLvl});
       console.log(JSON.stringify(this.state.maze)+"HERE");
       
          })
return disableMaze();
          function disableMaze(){
            var mazeLvl =JSON.stringify(this.state.maze);
            if(thismaze>1){
              document.getElementById("cloud1").style.filter = "grayscale(100%)";
            }
          }

  };


  handleButton = event => {
    event.preventDefault();
var mazeLvl =JSON.stringify(this.state.maze);
    
    function lvl1() {

      let id = null;
      const elem = document.getElementById("animate");
      let pos = 0;
      clearInterval(id);
      id = setInterval(frame, 10);
      function frame() {
        if (pos === 70) {
          clearInterval(id);
          if (mazeLvl > 1) {
            setTimeout(lvl2, 2);
          }

        } else {
          pos++; console.log("move1 calleD" + pos);
          elem.style.top = pos + "px";
          elem.style.left = pos + "px";
        }
      }
    }
    function lvl2() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 70;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 260) {
          clearInterval(id);
          if (mazeLvl > 2) {
            setTimeout(lvl3, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.top = pos + "px";

        }
      }
    }

    function lvl3() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 260;
      let posL = 70;
      clearInterval(id);
      id = setInterval(frame, 6);
      function frame() {
        if (posT === 450 || posL === 240) {
          clearInterval(id);
          if (mazeLvl > 3) {
             setTimeout(lvl4, 2);
          }
        } else {
          posT++; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";console.log(posT);console.log(posL)

        }
      }
    }
    function lvl4() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 430;
      let posL = 240;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (posT === 150 || posL === 800) {
          clearInterval(id);
          if (mazeLvl >4) {
             setTimeout(lvl5, 2);
          }
        } else {
          posT--; 
          posL++;
          elem.style.top = posT + "px"; console.log(posT);console.log(posL)
          elem.style.left = posL + "px";
        }
      }
    }
    function lvl5() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 150;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 350) {
          clearInterval(id);
          if (mazeLvl > 5) {
            setTimeout(lvl6, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.top = pos + "px";

        }
      }
    }

    function lvl6() {

      let id = null;
      const elem = document.getElementById("animate");
      let posT = 350;
      let posL = 520;

      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (posT === 40) {
          clearInterval(id);
          if (mazeLvl > 6) {
           setTimeout(lvl7, 2);
          }

        } else {
          posT--; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";console.log(posL);console.log(posT)
        }
      }
    }
    function lvl7() {

      let id = null;
      const elem = document.getElementById("animate");
      let pos = 40;
     

      clearInterval(id);
      id = setInterval(frame, 4);
      function frame() {
        if (pos === 250) {
          clearInterval(id);
          if (mazeLvl > 7) {
            setTimeout(lvl8, 2);
          }

        } else {
          
          pos++;
          elem.style.top = pos + "px";
         
        }
      }
    }
    function lvl8() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 250;
      let posL = 830;
      clearInterval(id);
      id = setInterval(frame, 6);
      function frame() {
        if (posT > 400 ) {
          clearInterval(id);
          
        } else {
          posT++; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";

        }
      }
    }

    return lvl1();




  }





  render() {


    return (



      <div id="container">
       
        <p><button onClick={this.handleButton}>Click Me</button></p>
        <div id="maze">
          
          <div id='cloud1' >
            <p>Level 1</p>
            <img  id='cloud1img' alt="" onClick={this.handlePopUp} src={cloud1} />
          </div>
          <div id='cloud2' >
            <img  id='cloud2img' alt="" onClick={this.handlePopUp} src={cloud2} />
          </div>
          <div id='cloud3' >
            <img  id='cloud3img' alt="" onClick={this.handlePopUp} src={cloud3} />
          </div>
          <div id='cloud4' >
            <img  id='cloud4img' alt="" onClick={this.handlePopUp} src={cloud4} />
          </div>
          <div id='cloud5' >
            <img  id='cloud5img' alt="" onClick={this.handlePopUp} src={cloud5} />
          </div>
          <div id='cloud6' >
            <img  id='cloud6img' alt="" onClick={this.handlePopUp} src={cloud6} />
          </div>
          <div id='cloud7' >
            <img  id='cloud7img' alt="" onClick={this.handlePopUp} src={cloud7} />
          </div>
          <div id='cloud8' >
            <img  id='cloud8img' alt="" onClick={this.handlePopUp} src={cloud8} />
          </div>
         
           



          <div id="animate">
            <img id="imgAnimate" alt="" src={wisp} />
          </div>
         


        </div>

      </div>

    )
  }
}
