import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";
import Button from 'react-bootstrap/Button';
import $ from 'jquery'; 
import ReactCircleModal from 'react-circle-modal'

export default class BadgeAdmin extends React.Component {
  state = {
    data: [],
    seen: false
  }
  

  componentDidMount() {
      
    axios.get(`http://localhost:8081/quiz`)
      .then(res => {
        console.log(res.data.length);
        this.setState({ data: res.data });
        console.log(res.data[0])
        for (var i = 0; i < res.data.length; i++) {
            var quiz = res.data[i];
            console.log("quizid:" + quiz.quizID);
            console.log("total Marks:" + quiz.totalMarks);
            console.log("toalPoints:" + quiz.totalPoints);
            
            //cpmplie the data that the card needs for its creation
            var cardInfo = {
                "quizID" : quiz.quizID,
                "totalMarks" : quiz.totalMarks,
                "totalPoints" : quiz.totalPoints
               
            }
            console.log(" -----------Card Info data pack-----------");
            console.log(cardInfo);



            var newCard = createCard(cardInfo);
            $('#users').append(newCard);
       
      }

    
    });
    

    function createCard(cardInfo) {
        console.log(cardInfo)
        var card = `
        <div class="card" style=" width:60% ;margin-right:10px;">
        <div class="card-body">
           
           
        </div> <p class="card-text" style=" font-size:20px;margin-left:20px">Quiz ${cardInfo.quizID}</p>
        <div style="margin-left:20px "> 
        <div >
            Total Marks:${cardInfo.totalMarks}
        </div>
        <div >
            Total Points:${cardInfo.totalPoints}
        </div>
        <Link to={"/EditBadge?id=${cardInfo.quizID}"}>
                      <Button>Do quiz</Button>
                    </Link>
        
          </div>

        
         </div>
            `;

        return card;
    }



   
  }

 //NEED TO GET STUDENT ID!

 togglePop = () => {
  this.setState({
   seen: !this.state.seen
  });
 };


  handle = event => {
    this.setState({ badgeClassID: event.target.value });
  }

  handleURL = event => {
    this.setState({ pic_url: event.target.files[0], });
  }

  handleSubmit = event => {
    event.preventDefault();
    //data extraction (combining data)
    // file upload
    const storage = getStorage();
    const storageRef = ref(storage, 'img/' + this.state.pic_url.name);
    var file = this.state.pic_url;
    // Create file metadata including the content type
    /** @type {any} */
    const metadata = {
      contentType: this.state.pic_url.type,
    };
    console.log("yes");
    uploadBytes(storageRef, file, metadata);

    getDownloadURL(storageRef).then((downloadURL) => {
      console.log('File available at', downloadURL);

      const badge = {
        name: this.state.name,
        requirements: this.state.requirements,
        pic_url: downloadURL,
        badgeClassID: this.state.badgeClassID
      };
      console.log("BADGEEEE" + JSON.stringify(badge))

      const config = {
        headers: {
          'content-type': 'application/json'
        }
      }
      axios.post('http://localhost:8081/newBadge', badge, config)
        .then(res => {
          console.log(res);
          console.log(res.data);
          window.location.reload();
        })
    });

    
  }



  render() {
      
    const data = this.state.data;
    
