import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
export default class BadgeAdmin extends React.Component {
    //GET-----------------------------------
    state = {
        name: [],
        requirements: [],
        pic_url: [],
        badgeClassID: []
      }

    componentDidMount() {
      axios.get(`http://localhost:8081/badges`)
          .then(res => {
            const name = res.data[0].name;
            const requirements = res.data.requirements;
            const pic_url = res.data.pic_url;
            const badgeClassID = res.data.badgeClassID;
            console.log(name);
            this.setState({ name });
            this.setState({ requirements });
            this.setState({ pic_url });
            this.setState({ badgeClassID });
      })
    }
  //POST------------------------------------
  state = {
    name: '',
  }

  handleChange = event => {
    this.setState({ name: event.target.value });
  }

  handleSubmit = event => {
    event.preventDefault();

    const user = {
      name: this.state.name
    };

    axios.post(`https://localhost:8081/newBadge`, { user })
      .then(res => {
        console.log(res);
        console.log(res.data);
      })
  }

  render() {
    return (
