import Home from './components/Home';
import SignIn from './components/SignIn';
import {
  onAuthStateChanged,
} from "firebase/auth";
import { auth } from './firebase.js';
import 'bootstrap/dist/css/bootstrap.css';

function App() {
  const [user, setUser] = useState({});
  useEffect(() => {
    const authenticated = onAuthStateChanged(auth, (userAuth) => {
      const user = {
        uid: currentUser
      }

      if(user) {
        setUser(currentUser);
        console.log(currentUser);
      } else {
        setUser(null);
      }
    })
    return authenticated;
  }, []);

  return (
    <div>
      {user ? <Home /> : <SignIn />}
    </div>
  );
}

export default App;
