                                </Nav.Item>
                                <Nav.Item>
                                    <Nav.Link className="linkCustomise" eventKey="link-2">Streak</Nav.Link>
                                </Nav.Item>
                            </Nav>


                        </div>
                    </Col>
                    <Col>2 of 3</Col>
                    <Col>3 of 3</Col>
                </Row>


                {/* <input type="range" className="custom-range" id="customRange1" /> */}
            </Container>




            //             <div className='toatalContainer' >





            //                 {/* 

