import '../SignIn.css';
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword
} from "firebase/auth";
import { auth } from '../firebase.js';
import { useNavigate } from 'react-router-dom';
import React, { useState } from 'react';
import axios from 'axios';


const SignIn = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    let navigate = useNavigate();

    const register = async () => {
        try {
            const user = await createUserWithEmailAndPassword(
                auth,
                email,
                password
            );
            const config = {
                headers: {
                    'content-type': 'application/json'
                }
            }

            var dateData = JSON.stringify(user.user.metadata.createdAt);
            dateData = parseInt(JSON.parse(dateData));
            const personalDetails = {
                name: JSON.stringify(user.user.email),
                Uid: JSON.stringify(user.user.uid),
                lastLogin: dateData,
                type: 1
            };
            axios.post('https://ades-ca1-project.herokuapp.com/api/newStudent', personalDetails, config)
                .then(res => {
                    console.log(res);
                    console.log(res.data);
                    navigate('/studentDashboard');
            })
        } catch (error) {
            const errorCode = error.code;
            const errorMessage = error.message;
            console.log(errorCode);
            console.log(errorMessage);
        }
    };

    const login = async () => {
        try {
            const user = await signInWithEmailAndPassword(
                auth,
                email,
                password
            );
            JSON.stringify(user.user.uid);
            navigate('/studentDashboard');
        } catch (error) {
            const errorCode = error.code;
            const errorMessage = error.message;
            console.log(JSON.stringify(errorCode));
            console.log(JSON.stringify(errorMessage));
        }
    };

    return (
        <div className="signin">
            <h1>Sign in</h1>
            <input placeholder="email" type="email" onChange={(event) => {
                setEmail(event.target.value);
            }} />
            <input placeholder="password" type="password" onChange={(event) => {
                setPassword(event.target.value);
            }} />
            <button onClick={login}> Sign In</button>

            <h1>Sign up</h1>
            <input placeholder="email" type="email" onChange={(event) => {
                setEmail(event.target.value);
            }} />
            <input placeholder="password" type="password" onChange={(event) => {
                setPassword(event.target.value);
            }} />
            <button onClick={register}>Sign Up</button>
        </div>
    )
}
export default SignIn;
