import React from 'react';
import axios from 'axios';
import wisp from '../img/wisp.png';
import cloud1 from '../img/cloud1.png'; // with import
import '../css/maze.css';


export default class BadgeAdmin extends React.Component {



  componentDidMount() {

    axios.get(`http://localhost:8081/mazeContent`)
      .then(res => {
        console.log(res.data.length);
        this.setState({ data: res.data });

        console.log(res.data.length);

        this.setState({ data: res.data });


      });





  }


  handleButton = event => {
    event.preventDefault();

    var mazeLvl = 1;


    function myMove() {

      let id = null;
      const elem = document.getElementById("animate");
      let pos = 90;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 350) {
          clearInterval(id);
          if (mazeLvl >= 1) {
            setTimeout(myMove2, 2);
          }

        } else {
          pos++; console.log("move1 calleD" + pos);
          elem.style.top = pos + "px";
          elem.style.left = pos + "px";
        }
      }
    }
    function myMove2() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 350;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 800) {
          clearInterval(id);
          if (mazeLvl >= 2) {
            setTimeout(myMove3, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.top = pos + "px";

        }
      }
    }

    function myMove3() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 350;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 1000) {
          clearInterval(id);
          if (mazeLvl >= 3) {
            // setTimeout(myMove3, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.left = pos + "px";

        }
      }
    }



    return myMove();




  }





  render() {


    return (



      <div id="container">
        <img src={""}/>
        <p><button onClick={this.handleButton}>Click Me</button></p>
        <div id="maze">
          <div id="animate">
            <img id="imgAnimate" src={wisp} />
          </div>
          <img id='img' onClick={this.handlePopUp} src={cloud1} />


        </div>

      </div>

    )
  }
}
