import React from 'react';
import axios from 'axios';
import wisp from '../img/wisp.png';
import cloud1 from '../img/cloud1.png'; 
import cloud2 from '../img/cloud2.png'; 
import cloud3 from '../img/cloud3.png'; 
import cloud4 from '../img/cloud4.png'; 
import '../css/maze.css';


export default class BadgeAdmin extends React.Component {

  state = {
    data: []
}


  componentDidMount() {

    axios.get(`http://localhost:8081/mazeContent`)
      .then(res => {
        console.log(res.data.length);
        this.setState({ data: res.data });

        console.log(res.data.length);

        this.setState({ data: res.data });


      });





  }


  handleButton = event => {
    event.preventDefault();

    var mazeLvl = 10;


    function lvl1() {

      let id = null;
      const elem = document.getElementById("animate");
      let pos = 0;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 200) {
          clearInterval(id);
          if (mazeLvl > 1) {
            setTimeout(lvl2, 2);
          }

        } else {
          pos++; console.log("move1 calleD" + pos);
          elem.style.top = pos + "px";
          elem.style.left = pos + "px";
        }
      }
    }
    function lvl2() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 200;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 600) {
          clearInterval(id);
          if (mazeLvl > 2) {
            setTimeout(lvl3, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.top = pos + "px";

        }
      }
    }

    function lvl3() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 600;
      let posL = 200;
      clearInterval(id);
      id = setInterval(frame, 6);
      function frame() {
        if (posT === 800 || posL === 400) {
          clearInterval(id);
          if (mazeLvl > 3) {
             setTimeout(myMove4, 2);
          }
        } else {
          posT++; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";

        }
      }
    }
    function lvl4() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 800;
      let posL = 400;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (posT === 100 || posL === 1000) {
          clearInterval(id);
          if (mazeLvl >= 3) {
             setTimeout(myMove5, 2);
          }
        } else {
          posT--; 
          posL++;
          elem.style.top = posT + "px"; console.log(posT);console.log(posL)
          elem.style.left = posL + "px";
        }
      }
    }
    function lvl5() {
      let id = null;
      const elem = document.getElementById("animate");
      let pos = 200;
      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 800) {
          clearInterval(id);
          if (mazeLvl > 2) {
            setTimeout(myMove6, 2);
          }
        } else {
          pos++; console.log("move2 calleD" + pos);
          elem.style.top = pos + "px";

        }
      }
    }

    function lvl6() {

      let id = null;
      const elem = document.getElementById("animate");
      let posT = 800;
      let posL = 1000;

      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (posT === 100) {
          clearInterval(id);
          if (mazeLvl > 1) {
           setTimeout(myMove7, 2);
          }

        } else {
          posT--; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";console.log(posL)
        }
      }
    }
    function myMove7() {

      let id = null;
      const elem = document.getElementById("animate");
      let pos = 100;
     

      clearInterval(id);
      id = setInterval(frame, 2);
      function frame() {
        if (pos === 500) {
          clearInterval(id);
          if (mazeLvl > 1) {
            setTimeout(myMove8, 2);
          }

        } else {
          
          pos++;
          elem.style.top = pos + "px";
         
        }
      }
    }
    function myMove8() {
      let id = null;
      const elem = document.getElementById("animate");
      let posT = 500;
      let posL = 1700;
      clearInterval(id);
      id = setInterval(frame, 6);
      function frame() {
        if (posT >800 ) {
          clearInterval(id);
          
        } else {
          posT++; 
          posL++;
          elem.style.top = posT + "px";
          elem.style.left = posL + "px";

        }
      }
    }

    return lvl1();




  }





  render() {


    return (



      <div id="container">
       
        <p><button onClick={this.handleButton}>Click Me</button></p>
        <div id="maze">
          <div id='cloud1' >
            <img  id='cloud1img' alt="" onClick={this.handlePopUp} src={cloud1} />
          </div>
          <div id='cloud2' >
            <img  id='cloud2img' alt="" onClick={this.handlePopUp} src={cloud2} />
          </div>
          <div id='cloud3' >
            <img  id='cloud3img' alt="" onClick={this.handlePopUp} src={cloud3} />
          </div>
          <div id='cloud4' >
            <img  id='cloud4img' alt="" onClick={this.handlePopUp} src={cloud4} />
          </div>
           
          <div id="animate">
            <img id="imgAnimate" alt="" src={wisp} />
          </div>
         


        </div>

      </div>

    )
  }
}
