import React from 'react';
import axios from 'axios';
import wisp from '../css/wisp.png'; // with import
import '../css/maze.css';


export default class BadgeAdmin extends React.Component {
 


  componentDidMount() {

   
  



  }
 
 
  handleButton = event => {
    event.preventDefault();

    function myMove() {
        let id = null;
        const elem = document.getElementById("animate");   
        let pos =20;
        const mazeLvl = 3;
        clearInterval(id);
        id = setInterval(frame, 3);
        function frame() {
          if (pos === 350) {
            clearInterval(id);
          } else {
            pos++; 
            // elem.style.top = pos + "px"; 
            // elem.style.left = pos + "px"; 
            if (mazeLvl1) {
              elem.style.top = pos + "px"; 
            }
            if (mazeLvl===2 ){
              elem.style.left = pos + "px";
            }
           if( mazeLvl===3 ){
            elem.style.left = pos + "px";
            elem.style.top = pos + "px"; 
           }


          }
        }
      }
return myMove();



}





  render() {

 
    return (
    <div>
        <p><button onClick={this.handleButton}>Click Me</button></p> 

<div id ="container">
  <div id ="animate">
        <img id ="imgAnimate" src={wisp} />
  </div>

</div>
    </div>

    )
  }
}
