import React, { Component, Fragment } from 'react';
import Button from 'react-bootstrap/Button';
import { Modal } from 'react-bootstrap';
import axios from 'axios';

export default class QuizPopUp extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showModal: false,
            data: [],
            id:0,
            quizID:0
        };
    }

    isShowModal = (status) => {
        this.handleClose();
        this.setState({ showModal: status });
    }

    handleClose = () => {
        this.props.onPopupClose(false);
    }

    componentDidMount() {
      //  this.quizHistory();
      for (let i = 0; i < 10; i++) {
        this.setState({ labelCounter: ++this.state.labelCounter })

        console.log(this.state.labelCounter)
    }
      
    }
    componentDidUpdate(prevProps, prevState){
        if (prevState.quizID !== this.props.quizID) {
            //THERE IS A CHANGE
            this.quizHistory();
          }
    }
    
quizHistory = ()=>{
    //   axios.get('https://ades-ca1-project.herokuapp.com/api/summary/'  + this.state.quizID + '/' + this.state.id)
    //         .then(res => {
    //             this.setState({ data: res.data });
    //             console.log(this.props.id + 'STUDENT ID' + this.props.quizID)
    //         })
}

    render() {

        const data = this.state.data;

        return (
            <Fragment>
                <Modal show={this.props.showModalPopup} onHide={this.handleClose}
                    size="lg"
                    aria-labelledby="contained-modal-title-vcenter"
                    centered  >
                    <Modal.Header closeButton>
                        <Modal.Title id="sign-in-title">
                            Summary
                         </Modal.Title>
                    </Modal.Header>
                    <Modal.Body>
                        <hr />
                        <div className="Summary">
                            <h3 className="p-3 text-center">Display Summary of Points</h3>
                            <table className="table table-striped table-bordered">
                                <thead>
                                    <tr>
                                        <th>Marks Awarded</th>
                                        <th>Points Earned</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {data && data.map(item =>
                                        <tr key={item.quizHistoryID}>
                                            <td>{item.pointsEarned}</td>
                                            <td>{item.marksEarned}</td>
                                        </tr>
                                    )}
                                </tbody>
                            </table>
                            <Button onClick={() => this.isShowModal(true)}>Close</Button>
                        </div>
                    </Modal.Body>
                </Modal >
            </Fragment >
        )
    }
}
