import React from 'react';
import axios from 'axios';
import { Link, } from 'react-router-dom';
import '../css/pop-up.css';

import Button from 'react-bootstrap/Button';

export default class BadgeAdmin extends React.Component {
  state = {
    data: [],

  }


  componentDidMount() {

    axios.get(`http://localhost:8081/quiz`)
      .then(res => {
        console.log(res.data.length);
        this.setState({ data: res.data });
        // console.log(res.data[0])
        // for (var i = 0; i < res.data.length; i++) {
        //   var quiz = res.data[i];
        //   console.log("quizid:" + quiz.quizID);
        //   console.log("total Marks:" + quiz.totalMarks);
        //   console.log("toalPoints:" + quiz.totalPoints);

        //   //cpmplie the data that the card needs for its creation
        //   var cardInfo = {
        //     "quizID": quiz.quizID,
        //     "totalMarks": quiz.totalMarks,
        //     "totalPoints": quiz.totalPoints,
        //     "event":this.handleButton

        //   }
        //   console.log(" -----------Card Info data pack-----------");
        //   console.log(cardInfo);



        //   var newCard = createCard(cardInfo);
        //   $('#users').append(newCard);

        // }
        console.log(res.data.length);

        this.setState({ data: res.data });


      });


    function createCard(cardInfo) {

      console.log(cardInfo)
      var card = `
        <div class="card" style=" width:60% ;margin-right:10px;">
        <div class="card-body">
           
           
        </div> <p class="card-text" style=" font-size:20px;margin-left:20px">Quiz ${cardInfo.quizID}</p>
        <div style="margin-left:20px "> 
        <div >
            Total Marks:${cardInfo.totalMarks}
        </div>
        <div >
            Total Points:${cardInfo.totalPoints}
        </div>
        <button onClick=${cardInfo.event}  type="submit" id="myBtn">Open Modal</button>
        
          </div>

        
         </div>
            `;

      return card;
    }




  }
  handleButton = event => {
    event.preventDefault();
    console.log("BUTTON CLICKED");
    // Get the modal
    var modal = document.getElementsByClassName("myModal");

    // Get the button that opens the modal
    var btn = document.getElementsByClassName("myBtn");
console.log(btn)
    // Get the <span> element that closes the modal
    var span = document.getElementsByClassName("close")[0];

    // When the user clicks the button, open the modal 
    btn.onclick = function () {
      modal.style.display = "block";
    }

    // When the user clicks on <span> (x), close the modal
    span.onclick = function () {
      modal.style.display = "none";
    }
    if (event.target === modal) {
      modal.style.display = "none";
    }
  }

  //NEED TO GET STUDENT ID!







  render() {

    const data = this.state.data;


    // When the user clicks anywhere outside of the modal, close it

    return (

      <div>
        <div id="users" class="row">


        </div>
        <div>


          {/* <button onClick={this.handleButton}  type="submit" id="myBtn">Open Modal</button>
 */}

          

        </div>
        <table class="table">
                
                {data && data.map(item =>
                  <tr key={item.quizID} id='tableRow' class="spaceUnder">
                    <td>{item.quizID}</td>
                    <td>{item.totalMarks}</td>
                    <td>{item.totalPoints}</td>
                  
                    <td>
                    <button onClick={this.handleButton} datain type="submit" className="myBtn" >Open Modal</button>
                    <div className="myModal" class="modal">


            <div class="modal-content">
              <span class="close">&times;</span>
              <p>Some text in the Modal..</p>
            </div>
          </div>
                    </td>
                  </tr>
                )}
              </table>

      </div>
    )
  }
}
