import React from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
import '../css/Table&Add.css';
import ModalPopup from './EditBadge';
export default class MazeAdmin extends React.Component {

    state = {
        data: [],
        mazeLvl: '',
        points: '',
        showModalPopup:false
    }

    componentDidMount() {
        axios.get('http://localhost:8081/mazeContent')
            .then(res => {
                this.setState({ data: res.data });
            })
    }
   

    isShowPopup = (status, mazeLvl, name) => {
        this.setState({ showModalPopup : status });
        this.setState({ badgeID : badgeID });
        this.setState({ name : name});
        
    }
  



    render() {
        const data = this.state.data;
        return (
            <div id="body">
            <div id="div">
            <div className="viewMazeLvl">
                <h3 className="p-3 text-center">Map of Maze Content</h3>

                <table className="table table-striped table-bordered">
                    <thead>
                        <tr>
                            <th>Maze Level</th>
                            <th>Points</th>

                        </tr>
                    </thead>
                    

                        {data && data.map(item =>
                            <tr id='tableRow' class="spaceUnder">
                                <td>{item.mazeLvl}</td>
                                <td>{item.points}</td>
                                <td>
                                    <Link to={`/EditMazeContent?lvl=${item.mazeLvl}`}>
                                    <Button>Edit</Button>
                                    </Link>
                                </td>

                            </tr>
                        )}
                    
                </table>
            </div>
            </div>
            <ModalPopup
                    showModalPopup={this.state.showModalPopup}
                    onPopupClose={this.isShowPopup}
                    mazeLvl={this.state.mazeLvl}
                    points={this.state.points}
                   
                ></ModalPopup>
      </div>
        )
    }
}
