import React, { Component, Fragment} from 'react';
import Button from 'react-bootstrap/Button';
import { Modal } from 'react-bootstrap';
import axios from 'axios';
import jQuery from 'jquery';

export default class MazePopup extends Component {

    constructor(props) {
        super(props);
        this.state = {
            showModal : false,
            newLevel:false,
            currentPts : 0,
            totalPts : 0,
            mazeLvl: 0,
            
        };
    }
    
    // componentDidMount(){
    //     // Get student's points data'
      
        // axios.get('https://ades-ca1-project.herokuapp.com/api/points/' + this.props.id)
        // .then(res => {
        //     this.setState({ currentPts : res.data[0].redeemedPts });
        //     this.setState({ totalPts : res.data[0].totalPts});
        //     this.setState({ mazeLvl : res.data[0].mazeLvl});
        // })
    // }





    isShowModal = (status) => {
        this.handleClose();
        this.setState({ showModal : status});
    }

 

  //to get the students current Lvl
  studentCurrentPoints = (callback) => {
    axios.get('http://ades-ca1-project.herokuapp.com/api/points/' + this.props.id)
    .then(res => {
        console.log( res.data[0].redeemedPts + "res for redeemdedpoitns")
        var currentPts = res.data[0].redeemedPts ;
        var  totalPts = res.data[0].totalPts;
       var mazeLvl = res.data[0].mazeLvl;
        const quizPts = this.props.point;
        callback(currentPts, totalPts, mazeLvl, quizPts, this.props.id);
    }).catch();
    
  }
    handleComplete = event => {
        // Store student's points
        event.preventDefault(); 
        
   this.studentCurrentPoints(function(currentPts, totalPts, mazeLvl, quizPts,studentID){
          
        const currentPoints = (currentPts + quizPts);
        const totalPoints = (totalPts + quizPts);
      
         mazeLvl = (parseInt(mazeLvl) + 1);
    
        // Do the calculation
        // this.setState({ currentPts : currentPoints});
        // this.setState({ totalPts : totalPoints});
        // this.setState({ mazeLvl : mazeLvl});
        // Update student's table
        const data = {
            currentPts : currentPoints,
            totalPts : totalPoints,
            mazeLvl: mazeLvl
        }
console.log(JSON.stringify(data) + "DATA SENT FOR PUT")
        const config = {
            headers: {
                'content-type':'application/json'
            }
        }

        axios.put('http://ades-ca1-project.herokuapp.com/api/points/' + studentID, data, config)
        .then(res => {
            console.log(res);
            console.log(res.data);
        })

        const points = {
            ptsAwarded : quizPts,
            eventID : 2
        }

        // Insert points history
        axios.post('http://ades-ca1-project.herokuapp.com/api/ptsHistory/' + studentID, points, config)
        .then(res => {
            console.log(res);
            console.log(res.data);
            handleClose();
            window.alert("Points redeemed successfully");
            //updating the state 
            
            
        })

   })
  

  function handleClose () {
    this.handleClosing()
}
     

    
    }
    handleClosing = () => {
        this.props.onPopupClose(false,this.props.level);
       this.props.onNewLevel(true);
   }

    render() {
             
        return (
            <Fragment>  
                <Modal show={this.props.showModalPopup} onHide={this.handleClose} id={this.props.level} 
                    size="lg" aria-labelledby="contained-modal-title-vcenter" centered>  
                    <Modal.Header closeButton>  
                        <Modal.Title id="sign-in-title">  
                            Level {this.props.level}
                         </Modal.Title>  
                    </Modal.Header>  
                    <Modal.Body>  
                        <hr />  
                        <div className="Quiz"> 
                        <div>Points : {this.props.point} </div>
                        <Button onClick={this.handleComplete} >Complete</Button>
                        </div>  
                    </Modal.Body>  
                </Modal >  
            </Fragment >  
        )
    }
}
