          if (mazeLvl > 3) {
            setTimeout(lvl4(mazeLvl), 2);
          }
        } else {
          posT++;
          posL++;
          elem.style.top = posT + "%";
          elem.style.left = posL + "%"; console.log(posT); console.log(posL)

