      let id = null;
      const elem = document.getElementById("animate");
      
      let posT = 10;
      let posL = 75;
      elem.style.top = posT + "%";
      elem.style.left = posL + "%";

      clearInterval(id);
      id = setInterval(frame, 30);
      function frame() {
        if (posT === 50) {
          clearInterval(id);
          if (mazeLvl > 7) {
            setTimeout(lvl8(mazeLvl), 2);
          }

        } else {

          posT++;
          elem.style.top = posT + "%";

        }
      }
